generated.css:
body {
  background: radial-gradient(circle at 12.3% 19.3%, rgb(85, 88, 218) 0%, rgb(95, 209, 249) 100.2%);
  font-family: "Roboto", sans-serif;
}

a {
  color: #66bfbf;
}

.weather-app {
  background: #eaf6f6;
  max-width: 600px;
  margin: 45px auto;
  padding: 30px;
  border-radius: 16px;
  box-shadow: 0 30px 50px rgba(65, 50, 100, 0.08);
}

header {
  border-bottom: 1px solid #f9f7fe;
  padding: 0 0 30px 0;
}

.search-form-input {
  background-color: #f9f7fe;
  border: none;
  font-size: 16px;
  padding: 15px 20px;
  width: 80%;
  border-radius: 6px;
}

.search-form-button {
  margin-left: 5px;
  font-size: 16px;
  background-color: #f76b8a;
  color: white;
  border: none;
  padding: 15px 20px;
  border-radius: 6px;
}

main {
  padding: 30px 0;
}

.weather-app-data {
  display: flex;
  justify-content: space-between;
}

.weather-app-city {
  margin: 0;
  font-size: 38px;
  line-height: 48px;

}
.weather-app-details {
  color: rgba(39, 33, 66, 0.4);
  font-size: 16px;
  font-weight: 500;
  line-height: 24px;
}

.weather-app-details strong {
  color: #f65282;
}

.weather-app-temperature-container {
  display: flex;
}

.current-temperature-icon {
  width: 88px;
  height: 88px;
 }

.weather-app-temperature {
  font-size: 80px;
  line-height: 80px;
  font-weight: bold;
}

.weather-app-unit {
  margin-top: 6px;
  font-size: 28px;
}

.weather-forecast-date {
  text-align: center;
  color: rgba(39, 33, 66, 0.4);
  font-size: 16px;
  line-height: 20px;
  margin-bottom: 10px;
}

.weather-forecast-icon {
  font-size: 38px;
  text-align: center;
}

.weather-forecast-temperatures {
  margin-top: 10px;
  display: flex;
  justify-content: center;
  color: #f65282;
}

.weather-forecast-temperature {
  padding-left: 10px;
}

footer {
  border-top: 1px solid #f9f7fe;
  font-size: 14px;
  padding: 30px 0 0 0;
  color: rgba(39, 33, 66, 0.4);
}

.weather-forecast{
  display: flex;
  align-items: center;
  justify-content: space-between;
}
